import{r as d,o as c,c as t,a as e,b as r,F as s,d as o,e as i}from"./app.3c6c2d94.js";import{_ as p}from"./plugin-vue_export-helper.21dcd24c.js";var n="/setup-mac-for-developer/assets/phpstorm-drop-to-applications-install.5b0e8d81.png",h="/setup-mac-for-developer/assets/phpstorm-evaluate-for-free.b832c4d5.png",l="/setup-mac-for-developer/assets/phpstorm-simple-setting.e79f06fd.png";const m={},b=e("h1",{id:"phpstorm",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#phpstorm","aria-hidden":"true"},"#"),o(" PHPStorm")],-1),u=o("PhpStorm \u7684\u4E0B\u8F7D\u8BF7\u5230"),f={href:"https://www.jetbrains.com/phpstorm/download/download-thanks.html",target:"_blank",rel:"noopener noreferrer"},_=o("\u5B98\u65B9\u4E0B\u8F7D\u5730\u5740"),S=o("\uFF0C\u9ED8\u8BA4\u4F1A\u9009\u4E2D\u5F53\u524D\u7684\u64CD\u4F5C\u7CFB\u7EDF\u5E73\u53F0\uFF0C\u70B9\u51FB "),g=e("strong",null,"Download",-1),w=o(" \u5F00\u59CB\u4E0B\u8F7D\u3002"),x=i('<p>\u6BD4\u5982\uFF0C\u8FD9\u91CC\u4E0B\u8F7D\u7684\u662F <code>PhpStorm-2018.2.1.dmg</code> \u6587\u4EF6\u3002</p><p>\u53CC\u51FB\u4E0B\u8F7D\u597D\u7684 <code>PhpStorm-2018.2.1.dmg</code> \u6587\u4EF6\uFF0C\u51FA\u73B0\u4E0B\u9762\u7684\u754C\u9762</p><p><img src="'+n+'" alt=""></p><p>\u5C06 <code>PhpStorm</code> \u62D6\u62FD\u5230\u53F3\u8FB9\u7684 <code>Applications</code> \u6587\u4EF6\u5939\u91CC\u3002</p><p>\u62D6\u62FD\u5B8C\u6210\u4E4B\u540E\uFF0C\u6765\u5230 <code>Launchpad</code> \u4E2D\u627E\u5230 <code>PhpStorm</code> \u7684\u56FE\u6807\uFF0C\u5355\u51FB\u6253\u5F00\u5B83\u3002</p><p><img src="'+h+'" alt=""></p><p>\u5982\u4E0A\uFF0C\u8FD9\u91CC\u9009\u62E9\u8BD5\u7528 30 \u5929\u4F53\u9A8C\u3002</p><p>\u81F3\u6B64\uFF0C\u4E0B\u8F7D\u548C\u5B89\u88C5\u5DF2\u7ECF\u5B8C\u6210\u3002</p><h2 id="\u7B80\u5355\u914D\u7F6E" tabindex="-1"><a class="header-anchor" href="#\u7B80\u5355\u914D\u7F6E" aria-hidden="true">#</a> \u7B80\u5355\u914D\u7F6E</h2><p>\u4FEE\u6539\u9ED8\u8BA4\u4E3B\u9898 \u9ED8\u8BA4\u7684\u64CD\u4F5C\u754C\u9762\u662F\u767D\u8272\u7684\uFF0C\u4E00\u822C\u60C5\u51B5\u4E0B\u4F1A\u5C06\u5176\u4FEE\u6539\u4E3A\u9ED1\u8272\u3002</p><p>\u4F7F\u7528\u5FEB\u6377\u952E <code>Command + Shift + a</code> \u8F93\u5165 <code>theme:</code> \uFF0C\u70B9\u51FB\u56DE\u8F66\u8FDB\u5165\u914D\u7F6E\u754C\u9762 -&gt; <code>Theme</code> \u4E00\u680F\u4E2D\u9009\u62E9 <code>Darcula</code>\u3002</p><h3 id="\u9690\u85CF\u6216\u663E\u793A\u5DE5\u5177\u680F-toolbar" tabindex="-1"><a class="header-anchor" href="#\u9690\u85CF\u6216\u663E\u793A\u5DE5\u5177\u680F-toolbar" aria-hidden="true">#</a> \u9690\u85CF\u6216\u663E\u793A\u5DE5\u5177\u680F Toolbar</h3><p>\u63D0\u4F9B\u4E24\u79CD\u65B9\u5F0F\u64CD\u4F5C\uFF1A</p><ol><li><p>\u4F7F\u7528\u5FEB\u6377\u952E<code>Command + Shift + a</code> \u8F93\u5165<code>Toolbar</code>\uFF0C\u6309\u56DE\u8F66\u6765\u5207\u6362\u663E\u793A\u548C\u9690\u85CF\u3002</p></li><li><p><code>Views</code>-&gt;<code>Toolbar</code> \uFF0C\u70B9\u51FB\u9690\u85CF\uFF0C\u518D\u6B21\u70B9\u51FB\u5219\u663E\u793A\u3002</p></li></ol><h3 id="\u9690\u85CF\u6216\u663E\u793A\u5DE5\u5177\u6309\u94AE-tool-buttons" tabindex="-1"><a class="header-anchor" href="#\u9690\u85CF\u6216\u663E\u793A\u5DE5\u5177\u6309\u94AE-tool-buttons" aria-hidden="true">#</a> \u9690\u85CF\u6216\u663E\u793A\u5DE5\u5177\u6309\u94AE Tool Buttons</h3><p>\u63D0\u4F9B\u4E24\u79CD\u65B9\u5F0F\u64CD\u4F5C\uFF1A</p><ol><li><p>\u4F7F\u7528\u5FEB\u6377\u952E<code>Command + Shift + a</code> \u8F93\u5165<code>Tool Buttons</code>\uFF0C\u6309\u56DE\u8F66\u6765\u5207\u6362\u663E\u793A\u548C\u9690\u85CF\u3002</p></li><li><p><code>Views</code>-&gt;<code>Tool Buttons</code> \uFF0C\u70B9\u51FB\u9690\u85CF\uFF0C\u518D\u6B21\u70B9\u51FB\u5219\u663E\u793A\u3002</p></li></ol><h3 id="\u9690\u85CF\u6216\u663E\u793A\u72B6\u6001\u680F-status-bar" tabindex="-1"><a class="header-anchor" href="#\u9690\u85CF\u6216\u663E\u793A\u72B6\u6001\u680F-status-bar" aria-hidden="true">#</a> \u9690\u85CF\u6216\u663E\u793A\u72B6\u6001\u680F Status Bar</h3><p>\u63D0\u4F9B\u4E24\u79CD\u65B9\u5F0F\u64CD\u4F5C\uFF1A</p><ol><li><p>\u4F7F\u7528\u5FEB\u6377\u952E<code>Command + Shift + a</code> \u8F93\u5165<code>Status Bar</code>\uFF0C\u6309\u56DE\u8F66\u6765\u5207\u6362\u663E\u793A\u548C\u9690\u85CF\u3002</p></li><li><p><code>Views</code>-&gt;<code>Status Bar</code>\uFF0C\u70B9\u51FB\u9690\u85CF\uFF0C\u518D\u6B21\u70B9\u51FB\u5219\u663E\u793A\u3002</p></li></ol><h3 id="\u9690\u85CF\u6216\u663E\u793A\u6587\u4EF6\u5BFC\u822A-navigation-bar" tabindex="-1"><a class="header-anchor" href="#\u9690\u85CF\u6216\u663E\u793A\u6587\u4EF6\u5BFC\u822A-navigation-bar" aria-hidden="true">#</a> \u9690\u85CF\u6216\u663E\u793A\u6587\u4EF6\u5BFC\u822A Navigation Bar</h3><p>\u63D0\u4F9B\u4E24\u79CD\u65B9\u5F0F\u64CD\u4F5C\uFF1A</p><ol><li><p>\u4F7F\u7528\u5FEB\u6377\u952E<code>Command + Shift + a</code> \u8F93\u5165<code>Navigation Bar</code>\uFF0C\u6309\u56DE\u8F66\u6765\u5207\u6362\u663E\u793A\u548C\u9690\u85CF\u3002</p></li><li><p><code>Views</code> -&gt; <code>Navigation Bar</code> \u70B9\u51FB\u9690\u85CF\uFF0C\u518D\u6B21\u70B9\u51FB\u5219\u663E\u793A\u3002</p></li></ol><h3 id="\u663E\u793A\u6216\u8005\u9690\u85CF\u4EE3\u7801\u884C\u53F7-line-numbers" tabindex="-1"><a class="header-anchor" href="#\u663E\u793A\u6216\u8005\u9690\u85CF\u4EE3\u7801\u884C\u53F7-line-numbers" aria-hidden="true">#</a> \u663E\u793A\u6216\u8005\u9690\u85CF\u4EE3\u7801\u884C\u53F7 Line Numbers</h3><p>\u4F7F\u7528\u5FEB\u6377\u952E <code>Command + Shif + a</code> \u8F93\u5165 <code>Show Line Numbers</code>\uFF0C\u6309\u56DE\u8F66\u6765\u5207\u6362\u663E\u793A\u548C\u9690\u85CF\u3002</p><h3 id="\u663E\u793A\u6216\u9690\u85CF\u6587\u4EF6\u5BFC\u822A-breadcrumbs" tabindex="-1"><a class="header-anchor" href="#\u663E\u793A\u6216\u9690\u85CF\u6587\u4EF6\u5BFC\u822A-breadcrumbs" aria-hidden="true">#</a> \u663E\u793A\u6216\u9690\u85CF\u6587\u4EF6\u5BFC\u822A Breadcrumbs</h3><p>\u63D0\u4F9B\u4E24\u79CD\u65B9\u5F0F\u64CD\u4F5C\uFF1A</p><ol><li><p>\u4F7F\u7528\u5FEB\u6377\u952E<code>Command + Shift + a</code> \u8F93\u5165 <code>Show Breadcrumbs</code> \uFF0C\u70B9\u51FB\u56DE\u8F66\u6765\u5207\u6362\u663E\u793A\u548C\u9690\u85CF\u3002</p></li><li><p>\u6765\u5230 <code>Preferences</code>\uFF08\u5FEB\u6377\u952E<code>Command + ,</code>\uFF09-&gt; <code>Editor</code> -&gt; <code>General</code> -&gt; <code>Breadcrumbs</code>\uFF0C\u5C06\u53F3\u4FA7\u914D\u7F6E\u4E2D\u7684 <code>Show breadcrumbs</code> \u524D\u9762\u7684\u52FE\u9009\u53BB\u6389\u5373\u53EF\u9690\u85CF\uFF0C\u52FE\u9009\u5219\u542F\u7528\u3002</p></li></ol><h3 id="\u663E\u793A\u6216\u9690\u85CF\u6587\u4EF6\u6807\u7B7E-tabs-placement" tabindex="-1"><a class="header-anchor" href="#\u663E\u793A\u6216\u9690\u85CF\u6587\u4EF6\u6807\u7B7E-tabs-placement" aria-hidden="true">#</a> \u663E\u793A\u6216\u9690\u85CF\u6587\u4EF6\u6807\u7B7E Tabs Placement</h3><p>\u4F7F\u7528\u5FEB\u6377\u952E <code>Command + Shift + a</code> \u8F93\u5165 <code>Tabs Placement</code>, \u70B9\u51FB <code>Tabs Placement: None</code> \u6765\u9690\u85CF\u3002</p><p>\u5982\u679C\u9700\u8981\u663E\u793A\u53EF\u4EE5\u6839\u636E\u9700\u8981\u9009\u62E9\u5DE6\u4FA7 <code>Tabs Placement: Left</code>\uFF0C\u53F3\u4FA7 <code>Tabs Placement: Right</code> \uFF0C\u4E0A\u9762 <code>Tabs Placement: Top</code> \u548C\u5E95\u90E8 <code>Tabs Placement: Bottom</code>\u3002</p><h3 id="\u663E\u793A\u6216\u9690\u85CF\u4EE3\u7801\u5C0F\u5730\u56FE-codeglance" tabindex="-1"><a class="header-anchor" href="#\u663E\u793A\u6216\u9690\u85CF\u4EE3\u7801\u5C0F\u5730\u56FE-codeglance" aria-hidden="true">#</a> \u663E\u793A\u6216\u9690\u85CF\u4EE3\u7801\u5C0F\u5730\u56FE CodeGlance</h3><p>\u4EE3\u7801\u5C0F\u5730\u56FE\u4F7F\u7528\u63D2\u4EF6 <code>CodeGlance</code> \uFF0C\u5B89\u88C5\u63D2\u4EF6\u8FD9\u91CC\u4E0D\u4ECB\u7ECD\uFF0C\u53EA\u4ECB\u7ECD\u5982\u4F55\u9690\u85CF\u6216\u663E\u793A\u5C0F\u5730\u56FE\u3002</p><p>\u4F7F\u7528\u5FEB\u6377\u952E <code>Command + Shift + a</code> \u8F93\u5165 <code>Toggle CodeGlance</code>, \u6309\u56DE\u8F66\u6765\u5207\u6362\u663E\u793A\u548C\u9690\u85CF\u3002</p><p>\u6216\u8005\u4E5F\u53EF\u4EE5\u4F7F\u7528\u5FEB\u6377\u952E <code>Command + Shift + g</code> \u6765\u5FEB\u901F\u5207\u6362\u9690\u85CF\u548C\u663E\u793A\u3002</p><h3 id="\u663E\u793A\u6216\u9690\u85CF\u4EE3\u7801\u533A\u57DF\u7684\u6D4F\u89C8\u5668-browser-popup-in-the-editor" tabindex="-1"><a class="header-anchor" href="#\u663E\u793A\u6216\u9690\u85CF\u4EE3\u7801\u533A\u57DF\u7684\u6D4F\u89C8\u5668-browser-popup-in-the-editor" aria-hidden="true">#</a> \u663E\u793A\u6216\u9690\u85CF\u4EE3\u7801\u533A\u57DF\u7684\u6D4F\u89C8\u5668 browser popup in the editor</h3><p>\u4F7F\u7528\u5FEB\u6377\u952E<code>Command + Shift +a</code> \u8F93\u5165 <code>Show browser popup in the editor</code>\uFF0C\u8FDB\u5165 <code>Web Browsers</code> \u7684\u5207\u6362\uFF0C\u5C06\u5E95\u90E8 <code>Show browser popup in the editor</code> \u7684\u9009\u9879\u52FE\u9009\u53BB\u6389\u5373\u53EF\u3002</p><h3 id="\u663E\u793A\u6216\u9690\u85CF\u7A7A\u767D\u5B57\u7B26-whitespaces" tabindex="-1"><a class="header-anchor" href="#\u663E\u793A\u6216\u9690\u85CF\u7A7A\u767D\u5B57\u7B26-whitespaces" aria-hidden="true">#</a> \u663E\u793A\u6216\u9690\u85CF\u7A7A\u767D\u5B57\u7B26 WhiteSpaces</h3><p>\u4F7F\u7528\u5FEB\u6377\u952E <code>Command + Shift + a</code> \u8F93\u5165 <code>Show WhiteSpaces</code> \u6765\u5FEB\u901F\u5207\u6362\u663E\u793A\u548C\u9690\u85CF\u3002</p><h3 id="\u8BBE\u7F6E\u4EE3\u7801\u81EA\u52A8\u6362\u884C-use-soft-wraps" tabindex="-1"><a class="header-anchor" href="#\u8BBE\u7F6E\u4EE3\u7801\u81EA\u52A8\u6362\u884C-use-soft-wraps" aria-hidden="true">#</a> \u8BBE\u7F6E\u4EE3\u7801\u81EA\u52A8\u6362\u884C Use Soft Wraps</h3><p>\u4F7F\u7528\u5FEB\u6377\u952E <code>Command + Shift + a</code> \u8F93\u5165 <code>Use Soft Wraps</code> \u6765\u5FEB\u901F\u5207\u6362\u81EA\u52A8\u663E\u793A\u7684\u9009\u9879\u3002</p><h3 id="\u8BBE\u7F6E\u9884\u89C8" tabindex="-1"><a class="header-anchor" href="#\u8BBE\u7F6E\u9884\u89C8" aria-hidden="true">#</a> \u8BBE\u7F6E\u9884\u89C8</h3><p>\u4EE5\u4E0A\u8BBE\u7F6E\u5168\u90E8\u8FDB\u884C\u7B80\u5355\u7684\u7F16\u8F91\u5668\u8BBE\u7F6E\uFF0C\u53EF\u4EE5\u770B\u5230\u5982\u4E0B\u7684\u9884\u89C8\u754C\u9762</p><p><img src="'+l+'" alt=""></p>',44);function C(B,T){const a=d("ExternalLinkIcon");return c(),t(s,null,[b,e("p",null,[u,e("a",f,[_,r(a)]),S,g,w]),x],64)}var N=p(m,[["render",C]]);export{N as default};
